"use strict";
$(document).ready(function() {
    var t = $(".templatemo-accordion > li > ul").hide();
    
    $(".templatemo-accordion > li > a").click(function() {
        var a = $(this).next();
        if (!a.hasClass("active")) {
            t.removeClass("active").slideUp();
            a.addClass("active").slideDown();
        }
        return false;
    });

    $(".product-links-wap a").click(function() {
        var t = $(this).children("img").attr("src");
        $("#product-detail").attr("src", t);
        return false;
    });

    $("#btn-minus").click(function() {
        var t = $("#var-value").html();
        t = (t == "1") ? t : t - 1;
        $("#var-value").html(t);
        $("#product-quanity").val(t);
        return false;
    });

    $("#btn-plus").click(function() {
        var t = $("#var-value").html();
        t++;
        $("#var-value").html(t);
        $("#product-quanity").val(t);
        return false;
    });

    $(".btn-size").click(function() {
        var t = $(this).html();
        $("#product-size").val(t);
        $(".btn-size").removeClass("btn-secondary").addClass("btn-success");
        $(this).removeClass("btn-success").addClass("btn-secondary");
        return false;
    });
});